import React from "react";
import {Container, Typography, Box, Grid, Paper} from "@material-ui/core";
import {makeStyles, withStyles} from "@material-ui/core/styles";
import SvgIcon from "@material-ui/core/SvgIcon";
import Button from "@material-ui/core/Button";
import FilledInput from '@material-ui/core/FilledInput';
import FormControl from '@material-ui/core/FormControl';
import FormHelperText from '@material-ui/core/FormHelperText';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        background: '#5d62b5',
        marginTop: theme.spacing(10),
        marginBottom: theme.spacing(10),
        borderRadius: 30,
    },
    typographyHeader: {
        color: '#fff',
        textAlign: 'center',
        paddingTop: theme.spacing(7),
    },
    textFieldStyle: {
        paddingTop: theme.spacing(7),
        margin: 'auto',
        color: '#fff',
    },
}));

function Appointment() {
    const classes = useStyles();
    const [name, setName] = React.useState();

    const handleChange = event => {
        setName(event.target.value);
    };

    return (
        <div className="AppointmentBackgroundImage">
            <div className={classes.root}>
                <Container maxWidth="lg">
                    <Grid
                        container
                        justify="flex-start"
                        alignItems="flex-start"
                        direction="row"
                    >
                        <Grid item sm={6}>
                            <Paper elevation={7} className={classes.paper}>
                                <Typography component="div" className={classes.typographyHeader}>
                                    <Box fontSize="h4.fontSize" fontWeight="fontWeightBold">
                                        Make an appointment
                                    </Box>
                                </Typography>
                                <form>
                                    <FormControl variant="outlined" className={classes.textFieldStyle}>
                                        <InputLabel htmlFor="component-outlined" className={classes.textFieldStyle}>Name</InputLabel>
                                        <OutlinedInput className={classes.textFieldStyle} id="component-outlined" value={name} onChange={handleChange} label="Name" />
                                    </FormControl>
                                </form>
                            </Paper>
                        </Grid>
                    </Grid>
                </Container>
            </div>
        </div>
    )
}

export default Appointment;
