import MailIcon from '@material-ui/icons/Mail';

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    toolbarBackground: {
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
        borderRadius: 13,
    },
    appBarColor: {
        borderRadius: 13,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    menuButtonBeforeSearch: {
        flexGrow: 1,
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade('#f2726f', 0.9),
        '&:hover': {
            backgroundColor: fade('#f2726f', 1),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 120,
            '&:focus': {
                width: 200,
            },
        },
    },
    toolbarBackgroundMobile: {
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
    },
    inputInputMobile: {
        padding: theme.spacing(1, 1, 1, 5),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 60,
            '&:focus': {
                width: 120,
            },
        },
    },
    searchIconMobile: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#f2726f',
    },
    searchMobile: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade('#f2726f', 0.35),
        '&:hover': {
            backgroundColor: fade('#f2726f', 0.6),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    appBarMobile: {
        top: 'auto',
        bottom: 0,
        backgroundColor: '#f2726f',
    },
    growMobile: {
        flexGrow: 1,
    },
    rootXS: {
        display: 'flex',
    },
    appBarXS: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShiftXS: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButtonXS: {
        marginRight: theme.spacing(2),
    },
    hideXS: {
        display: 'none',
    },
    drawerXS: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaperXS: {
        width: drawerWidth,
    },
    drawerHeaderXS: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    contentXS: {
        flexGrow: 1,
        padding: theme.spacing(3),
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        marginLeft: -drawerWidth,
    },
    contentShiftXS: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
        marginLeft: 0,
    },
}));

const theme = createMuiTheme({
    typography: {
        button: {
            fontWeight: 500,
        },
    },
});

function Navigation() {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const theme = useTheme();
    const [open, setOpen] = React.useState(false);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    return (
        <div className={classes.root}>
            <Container>
                <Hidden smDown>
                    <AppBar position="static" className={classes.appBarColor}>
                        <Toolbar className={classes.toolbarBackground}>
                            <ThemeProvider theme={theme}>
                                <div className={classes.menuButton}>
                                    <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                        Services
                                    </Button>
                                    <Menu
                                        id="simple-menu"
                                        anchorEl={anchorEl}
                                        keepMounted
                                        open={Boolean(anchorEl)}
                                        onClose={handleClose}
                                    >
                                        <MenuItem onClick={handleClose}>Service 1</MenuItem>
                                        <MenuItem onClick={handleClose}>Service 2</MenuItem>
                                        <MenuItem onClick={handleClose}>Service 3</MenuItem>
                                    </Menu>
                                </div>
                            </ThemeProvider>
                                <div className={classes.menuButton}>
                                    <Button href="#">Specials</Button>
                                </div>
                                <div className={classes.menuButton}>
                                    <Button href="#">Facilities</Button>
                                </div>
                                <div className={classes.menuButton}>
                                    <Button href="#">Reviews</Button>
                                </div>
                                <ThemeProvider theme={theme}>
                                    <div className={classes.menuButton}>
                                        <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                            Shop
                                        </Button>
                                        <Menu
                                            id="simple-menu"
                                            anchorEl={anchorEl}
                                            keepMounted
                                            open={Boolean(anchorEl)}
                                            onClose={handleClose}
                                        >
                                            <MenuItem onClick={handleClose}>Product 1</MenuItem>
                                            <MenuItem onClick={handleClose}>Product 2</MenuItem>
                                            <MenuItem onClick={handleClose}>Product 3</MenuItem>
                                        </Menu>
                                    </div>
                            </ThemeProvider>
                            <div className={classes.menuButtonBeforeSearch}>
                                <Button href="#">About</Button>
                            </div>
                            <div className={classes.search}>
                                <div className={classes.searchIcon}>
                                    <SearchIcon />
                                </div>
                                <InputBase
                                    placeholder="Search…"
                                    classes={{
                                        root: classes.inputRoot,
                                        input: classes.inputInput,
                                    }}
                                    inputProps={{ 'aria-label': 'search' }}
                                />
                            </div>
                        </Toolbar>
                    </AppBar>
                </Hidden>
            </Container>
            <Hidden only={['lg', 'xl', 'md', 'xs']}>
                <AppBar position="static">
                    <Toolbar className={classes.toolbarBackgroundMobile}>
                        <ThemeProvider theme={theme}>
                            <div className={classes.menuButton}>
                                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                    Services
                                </Button>
                                <Menu
                                    id="simple-menu"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                                >
                                    <MenuItem onClick={handleClose}>Service 1</MenuItem>
                                    <MenuItem onClick={handleClose}>Service 2</MenuItem>
                                    <MenuItem onClick={handleClose}>Service 3</MenuItem>
                                </Menu>
                            </div>
                        </ThemeProvider>
                        <div className={classes.menuButton}>
                            <Button href="#">Specials</Button>
                        </div>
                        <div className={classes.menuButton}>
                            <Button href="#">Facilities</Button>
                        </div>
                        <div className={classes.menuButton}>
                            <Button href="#">Reviews</Button>
                        </div>
                        <ThemeProvider theme={theme}>
                            <div className={classes.menuButton}>
                                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                    Shop
                                </Button>
                                <Menu
                                    id="simple-menu"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                                >
                                    <MenuItem onClick={handleClose}>Product 1</MenuItem>
                                    <MenuItem onClick={handleClose}>Product 2</MenuItem>
                                    <MenuItem onClick={handleClose}>Product 3</MenuItem>
                                </Menu>
                            </div>
                        </ThemeProvider>
                        <div className={classes.menuButtonBeforeSearch}>
                            <Button href="#">About</Button>
                        </div>
                        <div className={classes.searchMobile}>
                            <div className={classes.searchIconMobile}>
                                <SearchIcon />
                            </div>
                            <InputBase
                                placeholder="Search…"
                                classes={{
                                    root: classes.inputRoot,
                                    input: classes.inputInputMobile,
                                }}
                                inputProps={{ 'aria-label': 'search' }}
                            />
                        </div>
                    </Toolbar>
                </AppBar>
            </Hidden>
            <Hidden smUp>
                <CssBaseline />
                <AppBar
                    position="fixed"
                    className={clsx(classes.appBarXS, {
                        [classes.appBarShiftXS]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            className={clsx(classes.menuButtonXS, open && classes.hideXS)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Persistent drawer
                        </Typography>
                    </Toolbar>
                </AppBar>
                <Drawer
                    className={classes.drawerXS}
                    variant="persistent"
                    anchor="left"
                    open={open}
                    classes={{
                        paper: classes.drawerPaperXS,
                    }}
                >
                    <div className={classes.drawerHeaderXS}>
                        <IconButton onClick={handleDrawerClose}>
                            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                        </IconButton>
                    </div>
                    <Divider />
                    <List>
                        {['Services', 'Specials', 'Facilities', 'Reviews'].map((text, index) => (
                            <ListItem button key={text}>
                                <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                <ListItemText primary={text} />
                            </ListItem>
                        ))}
                    </List>
                    <Divider />
                    <List>
                        {['All mail', 'Trash', 'Spam'].map((text, index) => (
                            <ListItem button key={text}>
                                <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                <ListItemText primary={text} />
                            </ListItem>
                        ))}
                    </List>
                </Drawer>
            </Hidden>
        </div>
    );
}

Navigation.propTypes = {
    width: PropTypes.oneOf(['lg', 'md', 'sm', 'xl', 'xs']).isRequired,
};

export default withWidth()(Navigation);
