import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles, createMuiTheme, ThemeProvider, withStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import {Container, withWidth} from "@material-ui/core";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Hidden from "@material-ui/core/Hidden";
import PropTypes from 'prop-types';
import Fab from "@material-ui/core/Fab";
import MoreIcon from '@material-ui/icons/MoreVert';
import AddIcon from '@material-ui/icons/Add';


const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    toolbarBackground: {
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
        borderRadius: 13,
    },
    appBarColor: {
        borderRadius: 13,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    menuButtonBeforeSearch: {
        flexGrow: 1,
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade('#f2726f', 0.9),
        '&:hover': {
            backgroundColor: fade('#f2726f', 1),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 120,
            '&:focus': {
                width: 200,
            },
        },
    },
    toolbarBackgroundMobile: {
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
    },
    inputInputMobile: {
        padding: theme.spacing(1, 1, 1, 5),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 60,
            '&:focus': {
                width: 120,
            },
        },
    },
    searchIconMobile: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#f2726f',
    },
    searchMobile: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade('#f2726f', 0.35),
        '&:hover': {
            backgroundColor: fade('#f2726f', 0.6),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    appBarMobile: {
        top: 'auto',
        bottom: 0,
        backgroundColor: '#f2726f',
    },
    growMobile: {
        flexGrow: 1,
    },
}));

const theme = createMuiTheme({
    typography: {
        button: {
            fontWeight: 500,
        },
    },
});

function Navigation() {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <div className={classes.root}>
            <Container>
                <Hidden smDown>
                    <AppBar position="static" className={classes.appBarColor}>
                        <Toolbar className={classes.toolbarBackground}>
                            <ThemeProvider theme={theme}>
                                <div className={classes.menuButton}>
                                    <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                        Services
                                    </Button>
                                    <Menu
                                        id="simple-menu"
                                        anchorEl={anchorEl}
                                        keepMounted
                                        open={Boolean(anchorEl)}
                                        onClose={handleClose}
                                    >
                                        <MenuItem onClick={handleClose}>Service 1</MenuItem>
                                        <MenuItem onClick={handleClose}>Service 2</MenuItem>
                                        <MenuItem onClick={handleClose}>Service 3</MenuItem>
                                    </Menu>
                                </div>
                            </ThemeProvider>
                                <div className={classes.menuButton}>
                                    <Button href="#">Specials</Button>
                                </div>
                                <div className={classes.menuButton}>
                                    <Button href="#">Facilities</Button>
                                </div>
                                <div className={classes.menuButton}>
                                    <Button href="#">Reviews</Button>
                                </div>
                                <ThemeProvider theme={theme}>
                                    <div className={classes.menuButton}>
                                        <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                            Shop
                                        </Button>
                                        <Menu
                                            id="simple-menu"
                                            anchorEl={anchorEl}
                                            keepMounted
                                            open={Boolean(anchorEl)}
                                            onClose={handleClose}
                                        >
                                            <MenuItem onClick={handleClose}>Product 1</MenuItem>
                                            <MenuItem onClick={handleClose}>Product 2</MenuItem>
                                            <MenuItem onClick={handleClose}>Product 3</MenuItem>
                                        </Menu>
                                    </div>
                            </ThemeProvider>
                            <div className={classes.menuButtonBeforeSearch}>
                                <Button href="#">About</Button>
                            </div>
                            <div className={classes.search}>
                                <div className={classes.searchIcon}>
                                    <SearchIcon />
                                </div>
                                <InputBase
                                    placeholder="Search…"
                                    classes={{
                                        root: classes.inputRoot,
                                        input: classes.inputInput,
                                    }}
                                    inputProps={{ 'aria-label': 'search' }}
                                />
                            </div>
                        </Toolbar>
                    </AppBar>
                </Hidden>
            </Container>
            <Hidden only={['lg', 'xl', 'md', 'xs']}>
                <AppBar position="static">
                    <Toolbar className={classes.toolbarBackgroundMobile}>
                        <ThemeProvider theme={theme}>
                            <div className={classes.menuButton}>
                                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                    Services
                                </Button>
                                <Menu
                                    id="simple-menu"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                                >
                                    <MenuItem onClick={handleClose}>Service 1</MenuItem>
                                    <MenuItem onClick={handleClose}>Service 2</MenuItem>
                                    <MenuItem onClick={handleClose}>Service 3</MenuItem>
                                </Menu>
                            </div>
                        </ThemeProvider>
                        <div className={classes.menuButton}>
                            <Button href="#">Specials</Button>
                        </div>
                        <div className={classes.menuButton}>
                            <Button href="#">Facilities</Button>
                        </div>
                        <div className={classes.menuButton}>
                            <Button href="#">Reviews</Button>
                        </div>
                        <ThemeProvider theme={theme}>
                            <div className={classes.menuButton}>
                                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                                    Shop
                                </Button>
                                <Menu
                                    id="simple-menu"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                                >
                                    <MenuItem onClick={handleClose}>Product 1</MenuItem>
                                    <MenuItem onClick={handleClose}>Product 2</MenuItem>
                                    <MenuItem onClick={handleClose}>Product 3</MenuItem>
                                </Menu>
                            </div>
                        </ThemeProvider>
                        <div className={classes.menuButtonBeforeSearch}>
                            <Button href="#">About</Button>
                        </div>
                        <div className={classes.searchMobile}>
                            <div className={classes.searchIconMobile}>
                                <SearchIcon />
                            </div>
                            <InputBase
                                placeholder="Search…"
                                classes={{
                                    root: classes.inputRoot,
                                    input: classes.inputInputMobile,
                                }}
                                inputProps={{ 'aria-label': 'search' }}
                            />
                        </div>
                    </Toolbar>
                </AppBar>
            </Hidden>
            <Hidden smUp>
                <CssBaseline />
                <AppBar
                    position="fixed"
                    className={clsx(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            className={clsx(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Persistent drawer
                        </Typography>
                    </Toolbar>
                </AppBar>
                <Drawer
                    className={classes.drawer}
                    variant="persistent"
                    anchor="left"
                    open={open}
                    classes={{
                        paper: classes.drawerPaper,
                    }}
                >
                    <div className={classes.drawerHeader}>
                        <IconButton onClick={handleDrawerClose}>
                            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                        </IconButton>
                    </div>
                    <Divider />
                    <List>
                        {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                            <ListItem button key={text}>
                                <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                <ListItemText primary={text} />
                            </ListItem>
                        ))}
                    </List>
                    <Divider />
                    <List>
                        {['All mail', 'Trash', 'Spam'].map((text, index) => (
                            <ListItem button key={text}>
                                <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                                <ListItemText primary={text} />
                            </ListItem>
                        ))}
                    </List>
                </Drawer>
            </Hidden>
        </div>
    );
}

Navigation.propTypes = {
    width: PropTypes.oneOf(['lg', 'md', 'sm', 'xl', 'xs']).isRequired,
};

export default withWidth()(Navigation);
